const numberBtn = document.querySelectorAll('.number-button')
const hourEl = document.getElementById('hour')
const minuteEl = document.getElementById('minute')
const secondEl = document.getElementById('second')
const setBtn = document.getElementById('set')
const clearBtn = document.getElementById("clear")
const actionContainer = document.getElementById('action-button-container')
const subEl = document.getElementById('sub')
const monitorEl = document.getElementById('monitor')

let hour = 0
let minute = 0
let second = 0
let ms = 0
let time = ''

function digitToDisplay(int , ms=false){
    if(ms){
        if (ms < 10){
            return int < 10? '0' + int: int
        }else{
            return '' + int
        }
    }else{
    return int < 10? '0' + int: int
    }
}

function appendNumber(id, clear=false){
    if (hour >= 10 && !clear){
        return
    }

    time += id
    update()
}

function update(){
    hour = Math.floor(Number(time)/10000)
    minute = Math.floor(Number(time)/100)%100
    second = Number(time)%100

    hourEl.textContent = digitToDisplay(hour)
    minuteEl.textContent = digitToDisplay(minute)
    secondEl.textContent = digitToDisplay(second)
}


numberBtn.forEach(number => {
    number.addEventListener('click',function(){
        appendNumber(number.id)
    })
});


clearBtn.addEventListener('click', function(){
    time = ''
    appendNumber('0', true)
})

setBtn.addEventListener('click', function() {
    if(Number(time) === 0){
        alert('The time cannot be zero. Please ajust the time')
        return
    }else{
        document.getElementById('number-button-container').setAttribute('style', 'display:none;')
    }

    pauseBtn.setAttribute('style','display:none;')
    continueBtn.setAttribute('style','display:none;')
    startBtn.setAttribute('style', '')

    if (second >= 60){
        second -= 60
        minute += 1
    }
    if (minute >= 60){
        minute -= 60
        hour += 1
    }

    time = '' + digitToDisplay(hour) + digitToDisplay(minute) + digitToDisplay(second)
    update()

    actionContainer.setAttribute('style','')
})

//-------------------------------------------------------
const startBtn = document.getElementById('start')
const pauseBtn = document.getElementById('pause')
const continueBtn = document.getElementById('continue')
const resetBtn = document.getElementById('reset')
let interval


startBtn.addEventListener('click', function() {
    pauseBtn.setAttribute('style','')
    startBtn.setAttribute('style','display:none;')
    interval = setInterval(Countdown,10)
})

pauseBtn.addEventListener('click', function(){
    continueBtn.setAttribute('style','')
    pauseBtn.setAttribute('style','display:none;')
    clearInterval(interval)
})

continueBtn.addEventListener('click', function(){
    pauseBtn.setAttribute('style','')
    continueBtn.setAttribute('style','display:none;')

})


function Countdown(){
    if(Number(time) === 0 && ms === 0){
        clearInterval(interval)
        pauseBtn.setAttribute('style','display:none;')
        let snd = new Audio("data:audio/wav;base64,");  
        monitorEl.setAttribute('class', 'flash-animation')
        snd.play()
        

        return
    }
    ms--

    if(ms < 0){
        ms = 99
        if(second - 1 < 0){
            second = 59
            if(minute - 1 < 0){
                minute = 59
                hour -= 1
            }else{
                minute -= 1
            }
        }else{
            second -= 1
        }
    }

    time = '' + digitToDisplay(hour) + digitToDisplay(minute) + digitToDisplay(second)
    subEl.textContent = digitToDisplay(ms, true)
    update()
}

continueBtn.addEventListener('click', function(){
    interval = setInterval(Countdown,10)
})

resetBtn.addEventListener('click', function(){
    monitorEl.setAttribute('class', '')
    time = ''
    appendNumber('0', true)
    ms = 0
    subEl.textContent = digitToDisplay(ms, true)
    clearInterval(interval)
    document.getElementById('number-button-container').setAttribute('style', '')
    actionContainer.setAttribute('style','display: none;')


})







